    private void BtgSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtgSalvarActionPerformed
        
//Não funcionou, tentei!
           DefaultTableModel modelo = (DefaultTableModel)jTable1.getModel();
        
            String linha[] = new String[6];

            linha[0] = JtfCod.getText();
            linha[1] = JtfProduto.getText();
            linha[2] = JtfQuantidade.getText();
            linha[3] = JtfPrecodeCompra.getText();
            linha[4] = JtfPrecodeVenda.getText();

            double venda = Double.parseDouble(JtfPrecodeVenda.getText());
            double compra = Double.parseDouble(JtfPrecodeCompra.getText());
            double margem = ((venda - compra) / compra) * 100;

            linha[5] = Double.toString(margem);

            modelo.addRow(linha);
            JtfCod.setText("");
            JtfProduto.setText("");
            JtfQuantidade.setText("");
            JtfPrecodeCompra.setText("");
            JtfPrecodeVenda.setText("");
            JtfMargemdeVenda.setText("");
            JtfCod.requestFocus();
    }//GEN-LAST:event_BtgSalvarActionPerformed

