    private void BtgCancelarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtgCancelarActionPerformed
      //Funcionou,Eba!
        int response =JOptionPane.showConfirmDialog(null,"Deseja Cancelar?","Confirmação",
                JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
        if(response==JOptionPane.YES_OPTION){
        JtfCod.setText("");
        JtfProduto.setText("");
        JtfQuantidade.setText("");
        JtfPrecodeCompra.setText("");
        JtfPrecodeVenda.setText("");
        JtfMargemdeVenda.setText("");
        }
    }//GEN-LAST:event_BtgCancelarActionPerformed

